import { h } from 'preact';
import styles from './Button.module.css';

export const Button = ({
  loading = false,
  disabled = false, // Add this parameter
  children,
  size = "lg",
  variant = "primary",
  onClick
}) => {
  let padding, fontSize;
  switch (size) {
    case 'sm':
      padding = '8px 16px';
      fontSize = '14px';
      break;
    case 'md':
      padding = '12px 16px';
      fontSize = '16px';
      break;
    case 'lg':
    default:
      padding = '15px 32px';
      fontSize = '16px';
      break;
  }

  return (
    <label 
      onClick={!disabled && onClick} // Disable onClick if the button is disabled
      className={`${styles.customButton} ${loading || disabled ? styles.disabled : ''}`} // Apply the disabled class if loading or disabled
      style={{
        padding: padding,
        fontSize: fontSize,
        color: variant === "primary" ? "white" : "#4CAF50",
        background: variant === "primary" ? "#4CAF50" : "#f1faf7"
      }}
    >
      {children}
    </label>
  );
};