import { h } from 'preact';

export const Icon = ({ src, alt, size, onClick }) => {
	let newSize = 28;
	if (size === 'sm') {
		newSize = 8;
	}
	return (
		<img onClick={onClick} style={{display: "flex", cursor: onClick ? "pointer" : "", width: newSize, height: newSize}} src={src} alt={alt} />
	);
};
