import { ThemeContext } from "../../Theme";
import styles from "./NumberInput.module.css";
import { h } from 'preact';

export const NumberInput = ({ placeholder, value, onChange, min, max, step, width = 'auto', size = 'md' }) => (

  <ThemeContext.Consumer>
      {({ theme }) => (
        <div
          className={styles.selectContainer}
          data-size={size}
          style={{ color: theme.text }} // Apply text color here
        >
          <input
            type="number"
            placeholder={placeholder}
            onChange={onChange}
            value={value}
            className={`${styles.selectStyle} ${theme.name === 'dark' ? styles.darkModePlaceholder : ''}`}
            min={min}
            max={max}
            step={step}
            style={{ width, backgroundColor: theme.inputBackground, color: theme.text }} // Apply background color here
          />
        </div>
      )}
    </ThemeContext.Consumer>
  
);