import { h } from 'preact';
import styles from "./Tooltip.module.css";

export const Tooltip = ({ children, title, offsetX = "0px" }) => {
  return (
    <div 
      className={styles.tooltip} 
      data-tooltip={title}
      style={{ "--offset-x": offsetX }}
    >
      {children}
    </div>
  );
};