import { h } from 'preact';
import { ThemeContext } from '../../Theme';
import styles from "./Checkbox.module.css";
import { Text } from '../Text/Text';

export const Checkbox = ({ label, checked, onChange }) => {
  return (
    <ThemeContext.Consumer>
      {({ theme }) => (
        <div className={styles.checkboxContainer}>
          <input
            type="checkbox"
            id={`custom-checkbox-${label}`}
            onChange={onChange}
            checked={checked}
            className={`${styles.checkboxStyle} ${checked ? (theme?.name === 'dark' ? styles.checkedDark : styles.checkedLight) : ''}`}
          />
          {!!label && <label htmlFor={`custom-checkbox-${label}`} className={styles.labelStyle}><Text>{label}</Text></label>}
        </div>
      )}
    </ThemeContext.Consumer>
  );
};